<?php

namespace PaBundle\Repository;

/**
 * IdGlobalPhotosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class IdGlobalPhotosRepository extends \Doctrine\ORM\EntityRepository
{
    public function listAllByLineDate( $ligne, $date )
	{
		$db = $this->_em->getConnection();
	
		$query = "SELECT * FROM id_global_photos WHERE ligne='$ligne' AND date='$date' ";

		$stmt = $db->prepare($query);
		$stmt->execute();
		$result = $stmt->fetchAll();
	
		return $result;
	}
}
